file='COVID-19-geographic-disbtribution-worldwide-2020-04-12.txt'Comma=$IFSn=0infect=0die=0all=0for i in $(cat $file)doif [[ $i =~ "Thailand" ]] ;thenecho $iIFS=$','for j in $idoif [[ $n -ge 10 ]] ; thenn=`expr $n \* 0`fin=`expr $n + 1`case=`expr $n % 5`death=`expr $n % 6`if [[ $case -eq 0 && $n -ne 10 ]] ; theninfect=`expr $infect + $j`fiif [[ $death -eq 0 && $n -ne 10 ]] ; thendie=`expr $die + $j`fiif [[ $n -eq 10 ]] ; thenall=`expr $j`fidonefidoneecho -e "\033[32mThe percentage of the population in Thailand that is infected is\033[0m"awk "BEGIN {print (($infect)/($all))*100}"echo -e "\033[31mThe percentage of the population in Thailand that has died is\033[0m"awk "BEGIN {print (($die)/($all))*100}"exit 0